import { Switch, Route } from 'react-router-dom';

import Layout from './components/Layout/Layout';
import UserProfile from './components/Profile/UserProfile';
import AuthPage from './pages/AuthPage';
import HomePage from './pages/HomePage';
import { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [data,setData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  const url = "https://dummyjson.com/products/search?q";


  useEffect(() => {
    const fetchData = async (value) => {
      try {
        const response = await axios.get(`${url}=${value}&limit=10`); // Replace with your API endpoint
        setData(response.data);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };
  
    fetchData();
  }, []);
  
  return (
    <Layout>
      <Switch>
        <Route path='/' exact>
          <HomePage  data={data} isLoading={isLoading}/>
        </Route>
        <Route path='/auth'>
          <AuthPage  data={data} isLoading={isLoading}/>
        </Route>
        <Route path='/profile'>
          <UserProfile  data={data} isLoading={isLoading}/>
        </Route>
      </Switch>
    </Layout>
  );
}

export default App;
