// export default Layout;


const Layout = (props) => {
  const videoStyle = {
    backgroundSize: 'cover',
    position: 'fixed',
    top: 0,
    left: 0,
    width: 'auto',
    height: '100vh',
    zIndex: -1
  };
  return (
    <Fragment  >
      <MainNavigation />
      <main  >
        <video autoPlay muted loop style={videoStyle}>
          <source src={BGV} type="video/mp4" />
          Your browser does not support the video tag.
        </video>{props.children}
      </main>
      <Footer />
    </Fragment>
  );
};

export default Layout;
