import Typed from 'react-typed';
import Card from '../Card/Card';


const StartingPageContent = (props ) => {
  return (
    <div className="h-[85vh] flex-row items-center justify-center">
      <div className=" mx-auto w-10/12 lg:w-90  rounded-md  shadow-md p-10 text-center  flex-row justify-center items-center   my-5 mx-5 bg-gray-200">
        <h2 className='my-8 text-3xl md:text-5xl text-red-800  '>Welcome on Evrima!</h2>
        <Typed
                  strings={[
                      '  Search for products',
                      '  Search for categories',
                      '  Search for brands']}
                      typeSpeed={40}
                      backSpeed={50}
                      attr="placeholder"
                      loop >
                      

                      <div>
                        <input className="ml-2 bg-transparent text-gray-800  text-xl md:text-2xl  " type="text"/>
                        <input
                          type="text"
                          value={props.value}
                          onChange={props.change}
                          placeholder="Search for products..."
                        />
                        {isLoading ? (
                          <p>Loading suggestions...</p>
                        ) : (
                          <ul>
                            {suggestions.map((suggestion) => (
                              <li key={suggestion.id}>{suggestion.name}</li>
                            ))}
                          </ul>
                        )}
                    </div>
                  </Typed>
      </div>

      
    </div>
    
  );
};

export default StartingPageContent;
