import { useState } from 'react';

import classes from './AuthForm.module.css';

const AuthForm = () => {
  const [isLogin, setIsLogin] = useState(true);

  const switchAuthModeHandler = () => {
    setIsLogin((prevState) => !prevState);
  };

  return (
    <section className="{classes.auth} mx-auto my-12 w-95 max-w-[25rem] rounded-md bg-[#B31312] shadow-md p-10 text-center">
      <h1 class="text-center text-white">{isLogin ? 'Login' : 'Sign Up'}</h1>
      <form>
        <div className="{classes.control} mb-2">
          <label className='block text-white font-bold mb-2' htmlFor='email'>Your Email</label>
          <input className='w-full font-inherit bg-[#F9DBBB] text-[#ED2B2A] rounded-md border border-white p-1' type='email' id='email' required />
        </div>
        <div className="{classes.control} mb-2">
          <label className='block text-white font-bold mb-2' htmlFor='password'>Your Password</label>
          <input className='w-full font-inherit bg-[#F9DBBB] text-[#ED2B2A] rounded-md border border-white p-1' type='password' id='password' required />
        </div>
        <div className="{classes.actions} mt-6 flex flex-col items-center">
          <button className='cursor-pointer font-inherit text-white bg-[#EA906C] border border-[#EA906C] rounded-md py-2 px-10'>{isLogin ? 'Login' : 'Create Account'}</button>
          <button
            type='button'
            className="{classes.toggle} mt-4 bg-transparent text-[#EA906C] border-none py-1 px-6 hover:text-[#EA906C]"
            onClick={switchAuthModeHandler}
          >
            {isLogin ? 'Create new account' : 'Login with existing account'}
          </button>
        </div>
      </form>
    </section>
  );
};

export default AuthForm;
